mapper.select:
select uo.oid, uo.service_id, uo.user_id, uo.amount, uo.status, uo.service_time, uo.create_time, s.service_name, sp.service_provider_name from user_order uo, service s, service_provider sp where uo.service_id=s.sid and s.service_provider_id=sp.spid and uo.user_id=#{userId,jdbcType=INTEGER}
and uo.status=#{code,jdbcType=INTEGER}
and sp.service_provider_name like concat('%',#{keyword},'%')
union select uo.oid, uo.service_id, uo.user_id, uo.amount, uo.status, uo.service_time, uo.create_time, s.service_name, sp.service_provider_name from user_order uo, service s, service_provider sp where uo.service_id=s.sid and s.service_provider_id=sp.spid and uo.user_id=#{userId,jdbcType=INTEGER}
and s.service_name like concat('%',#{keyword},'%')
and uo.status=#{code,jdbcType=INTEGER}
